var PRECO = PRECO || {};

(function() {

var INPUT_TABLE = ["text", "number", "password", "file", "email", "search", "url", "tel", "date", "datetime", "datetime-local", "month", "time",  "week"];

window.addEventListener("load", function() {
	document.body.style.cursor = "default";
	document.body.style.webkitUserSelect = "none";
	document.body.style.webkitUserDrag = "none";
});

window.addEventListener("keydown", function(event) {
	if (!PRECO.DISABLE_PREVENT_HISTORY_BACK && event.keyCode == 8) {
		var target = event.target;
		var prevent = !target.isContentEditable;
		
		if ((target.tagName.toLowerCase() == "input" && INPUT_TABLE.indexOf(target.type.toLowerCase()) != -1) ||
			target.tagName.toLowerCase() == "textarea") {
			prevent = target.readOnly || target.disabled;
		}
		
		if (prevent) {
			event.preventDefault();
		}
    }
});

window.addEventListener("dragover", function(event) {
	event.stopPropagation();
	event.preventDefault();
});

window.addEventListener("dragstart", function(event) {
	event.stopPropagation();
	event.preventDefault();
});
	
window.addEventListener("drop", function(event) {
	event.stopPropagation();
	event.preventDefault();
});

PRECO.preventHistoryBack = true;

})();